function string:split(inSplitPattern, outResults)
  if not outResults then
      outResults = { }
  end
  local theStart = 1
  local theSplitStart, theSplitEnd = string.find( self, inSplitPattern, theStart )
  while theSplitStart do
      table.insert( outResults, string.sub( self, theStart, theSplitStart-1 ) )
      theStart = theSplitEnd + 1
      theSplitStart, theSplitEnd = string.find( self, inSplitPattern, theStart )
  end
  table.insert( outResults, string.sub( self, theStart ) )
  return outResults
end

function bool_to_number(value)
  return value and 1 or 0
end


local function textFilter(baseString)
local output2, count2 = string.gsub(baseString,"[^%d^-]", "")
return output2
end

local function textFilter2(baseString)
local output2, count2 = string.gsub(baseString,"[^%a]", "")
return output2
end

function tprint(tbl, indent)
  if not indent then indent = 0 end
  for k, v in pairs(tbl) do
    formatting = string.rep("  ", indent) .. k .. ": "
    if type(v) == "table" then
      print(formatting)
      tprint(v, indent+1)
    elseif type(v) == 'boolean' then
      print(formatting .. tostring(v))     
    else
      print(formatting .. v)
    end
  end
end

local IsChanged = false;

logSettings = {}

local function save_user_cfg()
table.insert(logSettings, gui.GetValue("rbot.antiaim.base"))
table.insert(logSettings, gui.GetValue("rbot.antiaim.left"))
table.insert(logSettings, gui.GetValue("rbot.antiaim.right"))
end

local function restore_user_cfg()
if(IsChanged) then
gui.Command("rbot.antiaim.base "..logSettings[1])
gui.Command("rbot.antiaim.left "..logSettings[2])
gui.Command("rbot.antiaim.right "..logSettings[3])
logSettings = {}
IsChanged = false
end
end

local function change_user_cfg()
if not(IsChanged) then

save_user_cfg();

if(textFilter2(gui.GetValue("rbot.antiaim.base")) == "Desync") or (textFilter2(gui.GetValue("rbot.antiaim.base")) == "DesyncJitter") then
