        local time, timeI, bTime, sTime, fps, fpso, bfps, tfps, filtered = {0, 0, 0,}, nil, 0, 0, 0, 0, 0, 0;
        callbacks.Register("Draw", "getTime", function()
            -- get time from web every 20 mins
            if sTime == 0 or ((sTime + 1200 < cache.time) and (cache.lp == nil or (not cache.lp:IsAlive() and cache.lpDeathTime + 5 < cache.time))) then
                local data = http.Get("https://time.is/");
                filtered, timeI = string.match(data, [[<time id="clock">(.-)<span.->(.-)</span></time>]]);
                filtered = filtered == nil and string.match(data, [[<time id="clock">(.-)</time>]]) or filtered;
                if filtered ~= nil then
                    for i, str in pairs(splitString(filtered, ":")) do
                        time[i] = tonumber(str);
                    end;
                    sTime = cache.time;
                end;
                bTime = cache.time;
            end;
            -- handles incrementation
            time[3], bTime = time[3] + cache.time - bTime, cache.time;
            if time[3] >= 60 then
                time[2], time[3], bTime = time[2] + 1, 0, cache.time;
            end;
            if time[2] >= 60 then
                time[1], time[2] = time[1] + 1, 0;
            end;
            timeI = timeI ~= nil and (time[1] >= 12 and (timeI == "PM" and "AM" or "PM") or timeI) or timeI;
            time[1] = timeI == nil and (time[1] >= 24 and 0 or time[1]) or (time[1] >= 12 and 0 or time[1]);
            -- fps
            fps = 0.9 * fps + (1.0 - 0.9) * globals.AbsoluteFrameTime();
            fpso = math.floor((1.0 / fps) + 0.5);
        end);
